const l=document.getElementById("imageInput"),c=document.getElementById("uploadedImg"),a=document.getElementById("downloadLink");l.addEventListener("change",function(d){const t=d.target.files[0];if(t){const n=new FileReader;n.onload=function(o){const e=document.createElement("img");e.src=o.target.result,e.alt="Uploaded Image",e.classList.add("w-full","h-full"),c.innerHTML="",c.appendChild(e)},n.readAsDataURL(t)}});const i=document.getElementById("outputImg");document.querySelector(".encodeBtn").addEventListener("click",()=>{const d=document.querySelector(".inputText").value,t=l.files[0];if(t){const n=new FileReader;n.onload=function(o){const e=document.createElement("img");e.src=steg.encode(d,o.target.result),e.alt="output",i.innerHTML="",e.classList.add("w-full","h-full"),i.appendChild(e),a.style.display="block",a.href=e.src,a.download=t.name},n.readAsDataURL(t)}});const r=document.querySelector(".outputText");document.querySelector(".decodeBtn").addEventListener("click",()=>{const d=l.files[0];if(d){const t=new FileReader;t.onload=function(n){r.innerHTML=steg.decode(n.target.result)},t.readAsDataURL(d)}});
